				int a=Integer.parseInt(array[0]);
				int b=Integer.parseInt(array[1]);
				int c=Integer.parseInt(array[2]);
				int d=Integer.parseInt(array[3]);
				int e=Integer.parseInt(array[4]);
				if(a==0 && b==0 && c==0 && d==0 && e==0)
					break;
				array=br.readLine().split(" ");
