			nc = sc.nextInt();

			int res;
			res = 1000000100;
			for(int i = 0; i <= na + nb + nc; i++) {
				if(i < nc) {
					if(i < d)
						res = Math.min(res, Math.min(i * c, d * e) + c * (nc-i) + b * nb + a * na);
					else
						res = Math.min(res, Math.min(i * c, i * e) + c * (nc-i) + b * nb + a * na);
				}
				else if(i < nc + nb) {
					if(i < d)
						res = Math.min(res, Math.min(nc * c + (i-nc) * b, d * e) + b * (nb -i+nc) + a * na);
