				int nc=Integer.parseInt(array[2]);

				int sum=0;
				if(nc>=d) {
					sum=na*a+nb*b+nc*e;
				} else {
					sum=na*a+nb*b+nc*c;
					if(na+nb+nc>=d) {
						int sum1;
						if(nb+nc>=d) sum1=na*a+(nb+nc-d)*b+d*e;
						else sum1=(na+nb+nc-d)*a+d*e;
						if(sum1<sum) sum=sum1;
					}
				}

				System.out.println(sum);
			} catch(IOException ex) {
				ex.printStackTrace();
				System.exit(-1);
			}
		}
	}
