			d=sc.nextInt();
			e=sc.nextInt();
			na=sc.nextInt();
			nb=sc.nextInt();
			nc=sc.nextInt();
			solve();
		}
	}

	void solve(){
		int tot;
		if(nc>=d){
			tot=e*nc+b*nb+a*na;
		}else{
			// nc<d
			int sum=c*nc, count=nc;
			int D=min(d-count, nb);
			nb-=D;
			sum+=b*D;
			count+=D;
			D=min(d-count, na);
			na-=D;
			sum+=a*D;
			count+=D;
//			debug(sum, count, na, nb, nc);

			sum=min(sum, d*e);
			tot=sum+b*nb+a*na;
		}
