using static System.Math;
using System;

public class Hello
{
    public static void Main()
    {
        while (true)
        {
            string[] line = Console.ReadLine().Trim().Split(' ');
            var a = int.Parse(line[0]);
            var b = int.Parse(line[1]);
            var c = int.Parse(line[2]);
            var d = int.Parse(line[3]);
            var e = int.Parse(line[4]);
            if (a == 0 && b == 0 && c == 0 && d == 0 && e == 0) break;
            line = Console.ReadLine().Trim().Split(' ');
            var na = int.Parse(line[0]);
            var nb = int.Parse(line[1]);
            var nc = int.Parse(line[2]);
            var ans = getAns(a, b, c, d, e, na, nb, nc);
            Console.WriteLine(ans);
        }
    }
    static int getAns (int a, int b, int c, int d , int e , int na, int nb, int nc)
    {
        var ncset = 0;
        if (nc >= d) { ncset = nc * e; nc = 0; }
        var pr1 = getPr1(a, b, c, d, e, na, nb, nc);
        var pr2 = getPr2(a, b, c, d, e, na, nb, nc);
        var pr3 = na * a + nb * b + nc * c;
        return Min(pr1, Min(pr2, pr3)) + ncset;
    }
    static int getPr1(int a, int b, int c, int d, int e, int na, int nb, int nc)
    {
        if (na + nb + nc < d) return int.MaxValue;
        var need = d - nc;
        if (nb >= need) return d * e + (nb - need) * b + na * a;
        else
        {
            var need2 = d - nc - nb;
            return d * e + (na - need2) * a;
        }
    }
    static int getPr2(int a, int b, int c, int d, int e, int na, int nb, int nc)
    {
        if (na + nb + nc < d) return d * e;
        var need = d - 1 - nc;
        if (nb >= need) return d * e + (nb - need) * b + na * a;
        else
        {
            var need2 = d  -1 - nc - nb;
            return d * e + (na - need2) * a;
        }
    }
}

