int solve_testcase() {
    size_t n;
    scanf("%zu", &n);
    if (n == 0)
        return 1;

    std::vector<std::string> w(n);
    for (size_t i=0; i<n; ++i) {
        char buf[16];
        scanf("%s", buf);
        w[i] = buf;
    }

    const size_t sph[]={5, 7, 5, 7, 7};
    for (size_t res=0; res<n; ++res) {
        size_t counter=0, index=0;
        for (size_t i=0; res+i<n; ++i) {
            if (counter + w[res+i].length() > sph[index])
                break;  // match failed

            counter += w[res+i].length();
            if (counter == sph[index]) {
                ++index;
                counter = 0;
            }

            if (index == 5)
                return !printf("%zu\n", res+1);
        }
    }

    // maybe not reached
    return 0;
}

int main() {
    while (!solve_testcase()) {}

    return 0;
}

