			if (s == seq[idx]) {
				s = 0;
				idx++;
				if (idx == seq.size())break;
			}
			else if (s > seq[idx]) {
				ng = true;
				break;
			}
		}
		if (!ng) {
			return i + 1;
		}
	}
	return -1;
}

