bool isword(int s,int n,vector<string> list){
    if(s==5)return true;
    int sum=0;
    bool flag;
    for(int i=n;i<list.size();i++){
        sum+=list[i].length();
        if(sum==seq[s]){
            flag=isword(s+1,i+1,list);
            break;
        }
        if(sum>seq[s]){
            flag=false;
            break;
        }
    }
    if(flag)return true;
    return false;
}

