class Solver {

public:
    Solver(int n) {
        N = n;
    }

    void solve() {
        REP(i, N) {
            string s;
            cin >> s;
            words.emplace_back(s.size());
        }
        int i = 0;
        while (!dfs(i, 0))i++;
        cout << i + 1 << endl;
