    bool dfs(int s, int p) {
        if (p == 5) {
            return true;
        }
        for (int len = 0, i = s; len <= tgt[p]; i++) {
            len += words[i];
            if (len == tgt[p]) {
                return dfs(i + 1, p + 1);
            }
        }

        return false;
    }
};

