#include<iostream>
#include<cstdlib>
#include<cstring>
#include<queue>
using namespace std;

const int maxn = 1000;
string str;
int n;
int mem[maxn];
int sum[maxn];
int memo[40];
queue<int> que;

bool check()
{
	int cnt = 0;
	if(memo[5]) cnt++;
	if(memo[12]) cnt++;
	if(memo[17]) cnt++;
	if(memo[24]) cnt++;
	if(memo[31]) cnt++;

	memo[5] = 0;
	memo[12] = 0;
	memo[17] = 0;
	memo[24] = 0;
	memo[31] = 0; 

	if(cnt == 5) return true;
	else return false;
}

int solve()
{
	for(int i = 0; i <= n; i++)
	{
		if(n >= 1) sum[i] = sum[i-1] + mem[i-1];
		else sum[i] = 0; 
	}

	for(int i = 0; i <= n; i++)
		for(int j = i+5; j <= n; j++)
		{
			if(sum[n] - sum[i] < 31) break;
			int d = sum[j]-sum[i];
			if(d > 31) break;
			else if(d == 31) que.push(i); 
		}

	int p, np; 
	while(!que.empty())
	{
		p = que.front(); que.pop();
		np = p + 1;
		//cout << "np " << np << endl;
		int total = 0;
		while(total!=31)
		{
			total += mem[p++];
			memo[total] = 1;
		}
		if(check()) return np;
	}
	return np;
}

bool Input()
{
	scanf("%d", &n); getchar();
	if(!n) return false;
	for(int i = 0; i < n; i++)
	{
		getline(cin, str); 
		mem[i] = str.length();
	}
	return true;
}

void Init()
{
	memset(mem, 0, sizeof(mem));
	memset(sum, 0, sizeof(sum));
	memset(memo, 0, sizeof(memo));
	while(!que.empty()) que.pop();
}

void Print()
{
	cout << "show" << endl;
	for(int i = 0; i <= n; i++)
	{
		printf("%d ", sum[i]);
	}
	cout << endl;
}

int main()
{
	//freopen("my.txt", "w", stdout);
	while(1)
	{
		Init();
		if(!Input()) break;
		printf("%d\n", solve());
		//Print();
	}
	return 0;
}

