#include <bits/stdc++.h>
using namespace std;

typedef pair<int, int> C;
typedef pair<int, C> iC;
#define fs first
#define sc second

string s;
bool overflow = false;

// index, number
pair<int, int> num(int i) {
  int ret = 0;
  while ('0' <= s[i] && s[i] <= '9') {
    int x = s[i] - '0';
    ret = 10 * ret + x;
    i++;
  }

  return make_pair(i, ret);
}

C multiplication(C a, C b) {
  return C(a.fs * b.fs - a.sc * b.sc, a.fs * b.sc + a.sc * b.fs);
}

C addition(C a, C b) {
  return C(a.fs + b.fs, a.sc + b.sc);
}

C subtraction(C a, C b) {
  return C(a.fs - b.fs, a.sc - b.sc);
}

void check_overflow(C a) {
  if (a.fs < -10000 || 10000 < a.fs) overflow = true;
  if (a.sc < -10000 || 10000 < a.sc) overflow = true;
}

// index, number
pair<int, C> dfs(int i = 0, C sum = C(0, 0)) {
  iC ans = make_pair(i, C(0, 0));

  while (i < s.size()) {
     if (s[i] == '(') {
      return dfs(i + 1);
    }

    if (s[i] == ')') {
      return iC(i, sum);
    }

    if (s[i] == '*') {
      iC a = dfs(i + 1);
      return iC(a.fs, multiplication(sum, a.sc));
    }

    if (s[i] == '+') {
      iC a = dfs(i + 1);
      return iC(a.fs, addition(sum, a.sc));
    }

    if (s[i] == '-') {
      iC a = dfs(i + 1);
      return iC(a.fs, subtraction(sum, a.sc));
    }

    if ('0' <= s[i] && s[i] <= '9') {
      pair<int, int> a = num(i);
      sum.fs += a.sc;
      i = a.fs;
    }

    if (s[i] == 'i') {
      return iC(i + 1, C(0, 1));
    }
  }
  return ans;
}

int main() {
  while (cin >> s) {
    iC ans = dfs();

    if (overflow) {
      cout << "overflow" << endl;
    } else {
      int real = ans.sc.fs;
      int imag = ans.sc.sc;

      if (real && imag) {
        printf("%d%c%di\n", real, (imag > 0 ? '+' : '-'), abs(imag));
      }
      else if (real) {
        printf("%d\n", real);
      }
      else if (imag) {
        printf("%di", imag);
      }
      else {
        puts("0");
      }
    }
  }
}

