#include <bits/stdc++.h>

using namespace std;

int solve(int n) {
    vector<string> words(n);
    for (int i = 0; i < n; i++) {
        cin >> words[i];
    }
    int a;
    int count = 0;
    while (1) {
        int first  = 0;
        int second = 0;
        int serd   = 0;
        int forth  = 0;
        int fifth  = 0;
        for (a = count; first < 5; a++) {
            first += words[a].size();
        }
        if (first != 5) {
            count++;
            continue;
        }
        for (; second < 7; a++) {
            second += words[a].size();
        }
        if (second != 7) {
            count++;
            continue;
        }
        for (; serd < 5; a++) {
            serd += words[a].size();
        }
        if (serd != 5) {
            count++;
            continue;
        }
        for (; forth < 7; a++) {
            forth += words[a].size();
        }
        if (forth != 7) {
            count++;
            continue;
        }
        for (; fifth < 7; a++) {
            fifth += words[a].size();
        }
        if (fifth != 7) {
            count++;
            continue;
        }
        return ++count;
    }
}

