    bool isShortPhrase(const std::string& phrases)
    {
        constexpr Ary<char, 5> eachLength = {5, 7, 5, 7, 7};

        int sum{}, current{};
        for (int i = 0; i < phrases.size(); ++i)
        {
            sum += (int)phrases[i];

            if (sum == eachLength[current])
            {
                if (current == eachLength.size() - 1)
                    return true;

                ++current;
                sum = 0;
            }
        }

        return false;
    }

