            goto next;
          }
          s.push(v[j]);
          continue;
        }
        if (s.top() > t[s.size()]) {
          break;
        }
        s.top() += v[j];
      }
      if (s.size() == 5 && s.top() == t[s.size()]) {
        cout << i + 1 << endl;
        goto next;
      }
    }
  next:;
  }
}

