int make(int w,int p){
  int l=0;
  int r=0;
  l=a[w].size();
  if(l==p){
    return w;
  }
  else{
    if(l<p){
      r=make(w+1,p-l);
      return r;
    }
    else{
      return -1;
    }
  }
}

