using namespace std;

bool checkCount(int& index, int wantNum, vector<int> v) {
    int sumLength = 0;
    // cout << "index: " << v[index] << " wantNum = " << wantNum << endl;
    for (int i = index; i < v.size(); i++) {
        sumLength += v[i];
        // cout << sumLength << endl;
        if (sumLength >= wantNum) {
            if (sumLength == wantNum) {
                index = i + 1;
                return true;
            }
            return false;
        }
    }
}

int solve(vector<int> v) {
    int right = 0;
    int sumLength = 0;
    for (int left = 0; left < v.size(); ++left) {
        while (right < v.size() && (sumLength + v[right] <= 5)) {
            sumLength += v[right];
            right++;
        }
        if (sumLength == 5) {
            int index = right;
            if (checkCount(index, 7, v)) {
                if (checkCount(index, 5, v)) {
                    if (checkCount(index, 7, v)) {
                        if (checkCount(index, 7, v)) {
                            return left + 1;
                        }
                    }
                }
            }
        }
        if (right == left)
            ++right;
        else
            sumLength -= v[left];
    }

    return 0;
}

