int32_t find_len(vector<uint32_t> &nums, uint32_t start, uint32_t len) {
  int32_t end = -1;
  int32_t index = start;
  int32_t remain = len;
  while (remain > 0 && index < nums.size()) {
    remain -= nums[index];
    index++;
  }

  if (remain == 0) {
    end = index;
  }

  return end;
}
