bool nya(std::vector<int> const& w, size_t k) {
  std::vector<int> haiku{5, 7, 5, 7, 7};
  int j = 0;
  for (size_t i = k; i < w.size(); ++i) {
    if (haiku[j] < w[i]) return false;
    haiku[j] -= w[i];
    if (haiku[j] == 0) {
      if (++j == 5) return true;
    }
  }
  return false;
}

