bool acceptable(const vector<int> w, int i) {
  const vector<int> tanku = {5, 7, 5, 7, 7};
  for (int ku : tanku) {
    int sum = 0;
    while (sum < ku) {
      sum += w[i++];
    }
    if (sum != ku) {
      return false;
    }
  }
  return true;
}

