		}

		boolean checker(int startWord)
		{
			int curWord = startWord;
			//can he do it?
			for(int i = 1; i <= 5; i++)
			{
				//set requirements
				int reqLet;
				if(i == 1 || i == 3) {reqLet = 5;}else {reqLet = 7;}

				//fill the bucket.
				int bucket = 0;

				while(bucket < reqLet)
				{
					if(curWord+1 > words.size())
					{
						//out of words
						return false;
					}
					bucket += words.get(curWord).length();

					if(bucket > reqLet)
					{
						//SPILL!!
						return false;
					}

					curWord += 1;
				}

			}
			return true;
		}

	}

