        let n: u16 = buf1.trim().parse().unwrap();
        if n == 0 {
            return;
        }

        let mut w: Vec<String> = Vec::new();
        for _ in 0..n {
            let mut buf2 = String::new();
            io::stdin().read_line(&mut buf2).ok();
            w.push(buf2.trim().to_string());
        }

        let mut current_size = 0;
        let mut current_rhythm_idx: usize = 0;
        'search:
        for i in 0..n {
            for j in i..n {
                current_size += w[j as usize].len();
                if current_size == SIZE_RHYTHM[current_rhythm_idx] {
                    if current_rhythm_idx == SIZE_RHYTHM.len() - 1 {
                        // passed
                        println!("{}", i + 1);
                        break 'search;
                    }

                    current_size = 0;
                    current_rhythm_idx += 1;
                } else if current_size > SIZE_RHYTHM[current_rhythm_idx] {
                    // failed
                    current_size = 0;
                    current_rhythm_idx = 0;
                    break;
                }
            }
        }
    }
}

use std::io::*;


fn solve(n: usize, ws: Vec<String>) -> usize{
    let array = [5, 7, 5, 7, 7];
    for index in 0..n {
        let mut count = 0;
        let mut pos = 0;
        for i in index..n {
            count+=ws[i].len();
            if count > array[pos] {
                break;
            }
            if count == array[pos] {
                count-=array[pos];
                pos = pos + 1;
                if pos == array.len() {
                    return index;
                }
            }
        }
    }
    0
}

fn main() {
    let mut buf = String::new();
    std::io::stdin().read_to_string(&mut buf).unwrap();
    let mut iter = buf.split_whitespace();

    loop {
        let n: usize = iter.next().unwrap().parse().unwrap();
        if n == 0 { break; }
        let ws: Vec<String> = (0..n)
            .map(|_| {iter.next().unwrap().parse().unwrap()})
            .collect();

        println!("{}", solve(n, ws)+1);
    }
}
