using namespace std;

const int INF = 100000;

struct UF{
    int par[202],r[202],cnt;
    void init(){
        for(int i = 0 ; i < 202 ; i ++){
            par[i] = i;
            r[i] = 0;
        }
        cnt = 0;
    }
    int find(int x){
        if(par[x] == x)return x;
        return par[x] = find(par[x]);
    }
    bool same(int x,int y){
        return find(x) == find(y);
    }
    void unit(int x,int y){
        if(same(x,y))return;
        cnt ++;
        x = find(x);
        y = find(y);
        if(r[x] < r[y]){
            par[x] = y;
        }
        else {
            par[y] = x;
            if(r[x] == r[y]){
                r[x] ++;
            }
        }
    }
}uf;

int main(){
    while(1){
        int n,m,k;
        scanf("%d%d%d",&n,&m,&k);
        if(n == 0 && m == 0 && k == 0)return 0;
        uf.init();
        vector<pair<int,pair<int,int>>> A,B;
        for(int i = 0 ; i < m ; i ++){
            int u,v,w;
