            if(l == 'A')A.push_back(pair<int,pair<int,int>>(w,pair<int,int>(u,v)));
            else B.push_back(pair<int,pair<int,int>>(w,pair<int,int>(u,v)));
            uf.unit(u,v);
        }
        if(uf.cnt < n-1){
            puts("-1");
            continue;
        }
        vector<pair<int,pair<int,int>>> _A,_B;
        sort(A.begin(),A.end());
        uf.init();
        for(int i = 0 ; i < A.size() ; i ++){
            if(uf.same(A[i].second.first,A[i].second.second)){}
            else {
                uf.unit(A[i].second.first,A[i].second.second);
                _A.push_back(A[i]);
            }
        }
        sort(B.begin(),B.end());
        uf.init();
        for(int i = 0 ; i < B.size() ; i ++){
            if(uf.same(B[i].second.first,B[i].second.second)){}
            else{
                uf.unit(B[i].second.first,B[i].second.second);
                _B.push_back(B[i]);
            }
        }
        if(k+_B.size() < n-1){
            puts("-1");
            continue;
        }

        /*puts("------_A------");
