        k -= n-1-_B.size();
        vector<int> vec;
        int ret = 0;
        for(int i = 0 ; i < _A.size() ; i ++)ret += _A[i].first;
        for(int i = 0 ; i < _B.size() ; i ++)ret += _B[i].first;

        //cout << ret << endl;

        for(int i = 0 ; i < _B.size() ; i ++){
            uf.init();
            for(int j = 0 ; j < i ; j ++){
                uf.unit(_B[j].second.first,_B[j].second.second);
            }
            for(int j = 0 ; j <= _A.size() ; j ++){
                if(j == _A.size()){
                    vec.push_back(INF);
                    break;
                }
                uf.unit(_A[j].second.first,_A[j].second.second);
                if(uf.same(_B[i].second.first,_B[i].second.second)){
                    vec.push_back(_A[j].first-_B[i].first);
                    ret -= _A[j].first;
