				if(use_a<k){
					e = A[p++];
					flag = true;
				}else e = B[q++];
			}else e = B[q++];
		}
		if(!uf.same(e.from,e.to)){
			uf.unite(e.from,e.to);
			cost += e.cost;
			if(flag)use_a++;
			use++;
		}
	}
	return use_a;
}
