using pi = pair<int,int>;

const int INF = 19191919;
const int N = 202;

int dp[N][N];

int main(){
    int n,m,k;
    while(scanf(" %d %d %d", &n, &m, &k),n){
        vector<vector<pi>> E(n);

        rep(i,m){
            int u,v,w;
            char l;
            scanf(" %d %d %d %c", &u, &v, &w, &l);

            int idx = max(u,v)-1;
            int isa = (l=='A');

            E[idx].pb({w,isa});
        }

        fill(dp[0],dp[N],INF);
        dp[0][0] = 0;
        for(int i=1; i<n; ++i)rep(j,k+1)if(dp[i-1][j]<INF){
            for(const auto e:E[i]){
                int w = e.fi;
                int nj = j+e.se;
                if(nj>k) continue;

                dp[i][nj] = min(dp[i][nj], dp[i-1][j]+w);
            }
        }

        int ans = dp[n-1][k];
        if(ans == INF) ans = -1;
        printf("%d\n", ans);
    }
    return 0;
}

