using namespace std;

template <int N>
struct union_find {
    int par[N];
    union_find() { for (int i = 0; i < N; ++i) par[i] = -1; }

    int find(int v) {
        if (par[v] < 0) return v;
        return par[v] = find(par[v]);
    }

    bool unite(int x, int y) {
        int xx = find(x), yy = find(y);
        if (xx == yy) return false;
        if (par[xx] < par[yy]) {
            par[xx] += par[yy];
            par[yy] = xx;
        } else {
            par[yy] += par[xx];
            par[xx] = yy;
        }
        return true;
    }

    bool same(int x, int y) {
        return find(x) == find(y);
    }
};

struct edge {
    int u, v, cost;
    char label;
    edge() {}
    edge(int u, int v, int cost, char label): u(u), v(v), cost(cost), label(label) {}

    bool operator < (const edge& o) const {
        return cost == o.cost ? label < o.label : cost < o.cost;
    }
};

int N, M, K;
vector<edge> edges;

pair<int, int> kruskal(vector<edge> es, int add_a) {
    for (edge& e : es)
        if (e.label == 'A') e.cost += add_a;

    union_find<200> uf;

    sort(begin(es), end(es));

    int weight_sum = 0, a_count = 0, b_count = 0;

    for (const edge& e : es) {
        if (uf.same(e.u, e.v)) continue;
        (e.label == 'A' ? a_count : b_count)++;
        uf.unite(e.u, e.v);
        weight_sum += e.cost;
    }

    if (a_count + b_count < N-1) return make_pair(-1, -1);
    return make_pair(weight_sum, a_count);
}

int solve() {

    auto pr = kruskal(edges, 0);

    if (pr.first == -1 && pr.second == -1) return -1;

    int l = -101010, r = 101010;
    while (l+1 < r) {
        int a = (l + r) / 2;

        pair<int, int> pr = kruskal(edges, a);

        ((pr.second < K) ? r : l) = a;
    }

    auto res_l = kruskal(edges, l);
    auto res_r = kruskal(edges, r);

    return (res_r.second <= K && K <= res_l.second) ? res_l.first - l * K : -1;
}


int main(void) {
    while (true) {
        cin >> N >> M >> K;
        edges.clear();
        if (N == 0 && M == 0 && K == 0) break;
        for (int i = 0; i < M; ++i) {
            int u, v, cost; char label;
            cin >> u >> v >> cost >> label; --u; --v;
            edges.emplace_back(u, v, cost, label);
        }

        cout << solve() << endl;
    }
    return 0;
}

