struct UnionFind {
    vector<int> data;
    UnionFind(int size) : data(size, -1) { }
    bool unionSet(int x, int y) {
        x = root(x); y = root(y);
        if (x != y) {
            if (data[y] < data[x]) swap(x, y);
            data[x] += data[y]; data[y] = x;
        }
        return x != y;
    }
    bool findSet(int x, int y) {
        return root(x) == root(y);
    }
    int root(int x) {
        return data[x] < 0 ? x : data[x] = root(data[x]);
    }
    int size(int x) {
        return -data[root(x)];
    }
};

using Elem = tuple<int, int, int, int>;

int kruskal(vector<vector<P>> graph, int diff){
    vi a_cnt(2);
    rep(v, n) for(auto & e : graph[v]) if(get<1>(e) == 0) get<0>(e) += diff;
    int ret = 0;
    rep(ai, 2){
        priority_queue<Elem, vector<Elem>, greater<Elem>> q;
        rep(v, n){
            for(auto & e : graph[v]){
                int u, w, l;
                tie(w, l, u) = e;

                l *= -1;
                e = P(w, l, u);
                assert(l == 0 or l == 1 or l == -1);

                if(v < u){
                    q.push(Elem(w, l, u, v));
                }
            }
        }

        UnionFind uf(n);
        ret = 0;
        int cnt = 0;
        while(q.size()){
            int w, l, v, u;
            tie(w, l, v, u) = q.top(); q.pop();

            assert(l == 0 or l == 1 or l == -1);

            if(uf.findSet(v, u)) continue;
            uf.unionSet(v, u);
            cnt++;

            ret += w;
            if(l == 0){
                a_cnt[ai]++;
            }
        }
        assert(cnt == n - 1);
    }

    ret -= diff * k;
    if(a_cnt[0] > a_cnt[1]) swap(a_cnt[0], a_cnt[1]);

    // cerr << a_cnt << endl;
    if(not (a_cnt[0] <= k and k <= a_cnt[1])) ret = (a_cnt[1] > k ? -1 : -2);

    return ret;
}

