struct UnionFind{
  vector<int> data, tree_size;
  UnionFind(int s):data(s,-1),tree_size(s,1) {}

  int root(int x){
    //debug(x);
    if(data[x]==-1) return x;
    return data[x]=root(data[x]);
  }

  bool set(int x,int y){
    x=root(x);
    y=root(y);
    if(x==y) return false;
    data[y]=x;
    tree_size[x] += tree_size[y];
    tree_size[y] = 0;
    return true;
  }

  bool check(int x,int y){
    x=root(x);
    y=root(y);
    return x==y;
  }

  int size(int x){
    return tree_size[root(x)];
  }

};


int n, m, k;
int u[SIZE], v[SIZE], w[SIZE], l[SIZE];

int check(int x){
  vector<pair<int,int> > vec;
  int sum = 0;

  for(int i=0;i<m;i++){
    vec.push_back({(w[i] + x * !l[i])*2 + l[i], i});
  }

  sort(vec.begin(), vec.end());

  int counter = 0, Acounter = 0;
  UnionFind uf(n);

  for(int i=0;i<m;i++){
    if(uf.set(u[vec[i].second], v[vec[i].second])){
      counter++;
      Acounter += !l[vec[i].second];
    }
  }

  if(counter == n-1)
    return Acounter;
  else
    return -1;
}

int make(int x){
  vector<pair<int,int> > vec;
  int sum = 0;

  for(int i=0;i<m;i++){
    vec.push_back({(w[i] + x * !l[i])*2 + l[i], i});
  }

  sort(vec.begin(), vec.end());

  int counter = 0, Acounter = 0;
  int limit = k;
  UnionFind uf(n);

  for(int i=0;i<m;i++){
    if(limit <= 0 && !l[vec[i].second]) continue;
    if(uf.set(u[vec[i].second], v[vec[i].second])){
      counter++;
      Acounter += !l[vec[i].second];
      sum += w[vec[i].second] + x * !l[vec[i].second];
      //limit -= !l[vec[i].second];
    }
  }

  assert(counter == n-1);

  return sum;
}

bool solve(){

  scanf("%d%d%d", &n, &m, &k);
  if(n == 0) return false;

  for(int i=0;i<m;i++){
    char c;
    scanf("%d%d%d %c", u+i, v+i, w+i, &c);
    u[i]--; v[i]--;
    l[i] = c - 'A';
    //debug(u[i]); debug(v[i]);
    assert(u[i] < n && v[i] < n);
  }

  int L = -200, R = 200;

  //cerr << check(L,0) << "-" <<  check(R+1,0) << endl;
  //cerr << check(L,1) << "-" <<  check(R+1,1) << endl;

  if(check(L) == -1 || (k < check(R) || check(L) < k)){
    puts("-1");
    return true;
  }

  while(L < R){
    int mid = (L+R+1)/2;

    if(check(mid) < k){
      R = mid - 1;
    }else{
      L = mid;
    }
  }

  //debug(L * k);

  int res = make(L);

  //debug(res);

  cout << res - L * k<< endl;

  return true;
}

