typedef tuple<int, int, int> P;

int n, k;

int prim (vector<vector<P>> graph, int diff) {
    rep(v, n){
        for(auto & e : graph[v]){
            int u, w, l;
            tie(w, l, u) = e;

            if(l == 1) continue;
            w += diff;
            e = P(w, l, u);
        }
    }

    priority_queue<P, vector<P>, greater<P> > q;
    for(auto & e : graph[0]) q.push(e);

    vi used(n);
    used[0] = true;

    int ret = 0, a_cnt = 0;
    int exa = 0;
    while(q.size()){
        int w, v, l;
        tie(w, l, v) = q.top(); q.pop();

        if(used[v]) continue;
        used[v] = true;

        ret += w;
        a_cnt += (l == 0 ? 1 : 0);

        for(auto & e : graph[v]){
            int nv; tie(ignore, ignore, nv) = e;
            if(not used[nv]){
                q.push(e);
                continue;
            }

            for(auto & ee : graph[nv]){
                int ww, vv, ll; tie(ww, ll, vv) = ee;
                if(vv == v and ll == (l^1) and ww == w){
                    exa++;
                    break;
                }
            }
        }
    }

    // cerr << a_cnt << endl;
    if(not (a_cnt - exa <= k and k <= a_cnt)) ret = (a_cnt > k ? -1 : -2);

    return ret;
}

