struct UnionFind{
  Int n;
  vector<Int> r,p;
  UnionFind(){}
  UnionFind(Int sz):n(sz),r(sz,1),p(sz,0){iota(p.begin(),p.end(),0);}
  Int find(Int x){
    return (x==p[x]?x:p[x]=find(p[x]));
  }
  bool same(Int x,Int y){
    return find(x)==find(y);
  }
  void unite(Int x,Int y){
    x=find(x);y=find(y);
    if(x==y) return;
    if(r[x]<r[y]) swap(x,y);
    r[x]+=r[y];
    p[y]=x;
  }
};

struct Kruskal{


  struct edge{
    Int from,to,cost,used;
    char color;
    edge(){}
    edge(Int from,Int to,Int cost,char color):
      from(from),to(to),cost(cost),used(0),color(color){}
    bool operator<(const edge& e) const{
      return cost<e.cost;
    }
  };

  Int n;
  vector<edge> edges;

  Kruskal(){}
  Kruskal(Int sz):n(sz){}

  void add_edge(Int u,Int v,Int c,char d){
    edges.emplace_back(u,v,c,d);
  }

  void input(Int m,Int offset=0){
    Int a,b,c;
    char d;
    for(Int i=0;i<m;i++){
      cin>>a>>b>>c>>d;
      add_edge(a+offset,b+offset,c,d);
    }
  }

  Int build(){
    sort(edges.begin(),edges.end());
    UnionFind uf(n+1);
    Int res=0,cnt=0;
    for(auto &e:edges){
      if(!uf.same(e.from,e.to)){
	res+=e.cost;
	uf.unite(e.from,e.to);
	e.used=1;
	cnt++;
      }
    }
    //cout<<cnt<<" "<<res<<endl;
    if(cnt+1!=n) return -1;
    return res;
  }

  Int count(char c){
    Int res=0;
    for(auto& e:edges)
      if(e.used&&e.color==c) res++;
    return res;
  }
};


signed main(){
  Int n,m,k;
  while(cin>>n>>m>>k,n){
    //cout<<n<<" "<<m<<" "<<k<<endl;
    Kruskal kr(n);
    kr.input(m,-1);
    Int ans=kr.build();
    if(ans<0){
      cout<<-1<<endl;
      continue;
    }

    auto es=kr.edges;
    Int A=kr.count('A');
    Int B=kr.count('B');

    auto check=[&](char c,char d,Int i){
      Int res=-1;
      UnionFind uf(n);
      for(Int j=0;j<m;j++)
	if(i!=j&&es[j].used)
	  uf.unite(es[j].from,es[j].to);

      for(Int j=0;j<m;j++)
	if(es[j].color==d&&!es[j].used)
	  if(!uf.same(es[j].from,es[j].to))
	    if(res<0||es[j].cost<es[res].cost)
	      res=j;

      return res;
    };

    //cout<<A<<" "<<B<<endl;
    while(A>k){
      Int res=-1,idx=-1,idy=-1;
      for(Int i=0;i<m;i++){
	if(es[i].color!='A'||!es[i].used) continue;
	Int tmp=check('A','B',i);
	if(tmp<0) continue;
	if(res<0||es[tmp].cost-es[i].cost<res){
	  res=es[tmp].cost;
	  idx=i;idy=tmp;
	}
      }
      if(idx<0) break;
      es[idx].used=0;ans-=es[idx].cost;
      es[idy].used=1;ans+=es[idy].cost;
      A--;B++;
    }
    while(A<k){
      Int res=-1,idx=-1,idy=-1;
      for(Int i=0;i<m;i++){
	if(es[i].color!='B'||!es[i].used) continue;
	Int tmp=check('B','A',i);
	if(tmp<0) continue;
	if(res<0||es[tmp].cost-es[i].cost<res){
	  res=es[tmp].cost;
	  idx=i;idy=tmp;
	}
      }
      if(idx<0) break;
      es[idx].used=0;ans-=es[idx].cost;
      es[idy].used=1;ans+=es[idy].cost;
      A++;B--;
    }
    if(A!=k) cout<<-1<<endl;
    else cout<<ans<<endl;
  }
  return 0;
}

