constexpr int inf = 1e9;

struct edge {
    int u, v, cost, type;
    bool operator<(const edge& other) const {
        return cost < other.cost;
    }
};

class union_find {
public:
    union_find(int n) : par(n, -1) {}
    int root(int x) { return par[x] < 0 ? x : par[x] = root(par[x]); }
    bool unite(int x, int y) {
        x = root(x), y = root(y);
        if(x == y) return false;
        if(par[x] > par[y]) swap(x, y);
        par[x] += par[y];
        par[y] = x;
        return true;
    }

private:
    vector<int> par;
};

int main() {
    int n, m, k;
    while(cin >> n >> m >> k, n) {
        vector<edge> es;
        for(int i = 0; i < m; ++i) {
            int u, v, w; char l;
            cin >> u >> v >> w >> l;
            es.push_back(edge{u - 1, v - 1, w, l == 'B'});
        }

        int ans = inf;
        for(int offset = -100; offset <= 100; ++offset) {
            auto tes = es;
            for(auto& e : tes) {
                if(e.type == 0) {
                    e.cost += offset;
                }
            }
            sort(begin(tes), end(tes),
                 [](auto const& e1, auto const& e2) {
                     return e1.cost < e2.cost || (e1.cost == e2.cost && e1.type < e2.type);
                 });
            int cnt1 = 0;
            int connect = 0;
            {
                union_find uf(n);
                for(auto& e : tes) {
                    if(uf.unite(e.u, e.v)) {
                        connect += 1;
                        cnt1 += e.type == 0;
                    }
                }
            }

            int cost = -offset * k;
            int cnt2 = 0;
            sort(begin(tes), end(tes),
                 [](auto const& e1, auto const& e2) {
                     return e1.cost < e2.cost || (e1.cost == e2.cost && e1.type > e2.type);
                 });
            {
                union_find uf(n);
                for(auto& e : tes) {
                    if(uf.unite(e.u, e.v)) {
                        cost += e.cost;
                        cnt2 += e.type == 0;
                    }
                }
            }
            if(cnt2 <= k && k <= cnt1 && connect == n - 1) {
                ans = min(ans, cost);
            }
        }

        cout << (ans == inf ? -1 : ans) << endl;
    }
}

