	cost = 0;
	use_a = 0;
	use = 0;
	while(p<A.size()&&q<B.size()){
		edge e;
		bool flag = false;
		if(p==A.size())e = B[q++];
		else if(q==B.size()){
			e = A[p++];
			flag = true;
		}else{
			if(A[p].cost+x<B[q].cost){
				e = A[p++];
				flag = true;
			}else if(A[p].cost+x==B[q].cost){
				if(use_a<k){
					e = A[p++];
					flag = true;
				}else e = B[q++];
			}else e = B[q++];
		}
		if(!uf.same(e.from,e.to)){
			uf.unite(e.from,e.to);
			cost += e.cost;
			if(flag)use_a++;
			use++;
		}
