			E.push_back({u,v,w,l});
		}
		ll inf = 1000000000000;
		if(kruscal(n,inf,0).second<k || kruscal(n,-inf,0).second>k){
			cout << -1 << endl;
		}else{
			ll r = inf,l = -inf;
			while(r - l>1){
				ll mid = (l + r)/2;
				if(kruscal(n,mid,0).second<k) l = mid;
				else r = mid;
			}
