struct UF{
    int n;
    vector<int> par,size;

    void init(int n_){
        n=n_;
        par.assign(n,-1);
        size.assign(n,1);

        for(int i=0;i<n;i++){
            par[i]=i;
        }
    }

    int root(int a){
        if(par[a]==a) return a;
        else return par[a]=root(par[a]);
    }

    void unite(int a,int b){
        if(root(a)!=root(b)){
            size[root(a)]+=size[root(b)];
            par[root(b)]=root(a);
        }
    }

    bool check(int a,int b){
        return root(a)==root(b);
    }
};

struct edge{
    int from;
    int to;
    ll cost;
    int type;
};

pair<ll,ll> check(ll plus,vector<edge> S,int N,int K){
    int M=si(S);
    for(int i=0;i<M;i++){
        if(!S[i].type) S[i].cost+=plus;
    }

    sort(all(S),[](auto a,auto b){
        if(a.cost==b.cost) return a.type<b.type;
        return a.cost<b.cost;
    });

    int cnt=0;
    ll sum=0;

    UF uf;
    uf.init(N);

    for(int i=0;i<M;i++){
        int a=uf.root(S[i].from),b=uf.root(S[i].to);
        if(uf.size[a]==N) break;

        if(!uf.check(a,b)){
            uf.unite(a,b);
            sum+=S[i].cost;
            if(!S[i].type) cnt++;
        }
    }

    if(K>N){
        return mp(sum-plus*(K-N),1);
    }

    if(uf.size[uf.root(0)]<N) return mp(0,0);

    if(plus==INF){
        if(cnt>K) return mp(0,0);
    }else if(plus==-INF){
        if(cnt<K) return mp(0,0);
    }

    if(cnt==K){
        return mp(sum-plus*K,1);
    }else if(cnt>K){
        return mp(1,0);
    }else{
        return mp(-1,0);
    }
}

