class union_find {
public:
    union_find(int n)
        : par_(n, -1)
    {}

    int root(int x) {
        return par_[x] < 0 ? x : par_[x] = root(par_[x]);
    }

    bool unite(int x, int y) {
        x = root(x); y = root(y);
        if(x == y) {
            return false;
        } else {
            if(par_[x] < par_[y]) {
                par_[x] += par_[y];
                par_[y] = x;
            } else {
                par_[y] += par_[x];
                par_[x] = y;
            }
            return true;
        }
    }

    int size(int x) {
        return -par_[root(x)];
    }

private:
    std::vector<int> par_;
};

struct edge {
    int u, v, cost;
    int kind;

    bool operator<(edge const& e) const {
        if(cost == e.cost) {
            return kind > e.kind;
        }
        return cost < e.cost;
    }
};

using result = pair<int, int>;

using edges = vector<edge>;

result kruskal(edges const& ES, int offset, int V) {
    auto es = ES;
    for(auto& e : es) {
        if(e.kind == 0) {
            e.cost += offset;
        }
    }
    sort(es.begin(), es.end());
    result res = make_pair(0, 0);
    union_find uf(V);
    for(auto& e : es) {
        if(uf.unite(e.u, e.v)) {
            res.second += e.kind == 0;
            res.first += e.cost;
        }
    }
    return res;
}


