#include <bits/stdc++.h>
using namespace std;
const int INF = 1000000000;
#define REP(i,s,n) for(int i=(int)(s);i<(int)(n);++i)
#define rep(i,n) REP(i, 0, n)
typedef long long int i64;
typedef pair<int, int> pint;

struct UnionFind {
    vector<int> data; int group;
    UnionFind(int N) : data(N, -1), group(N) {}
    void init(int N) {
        data.assign(N, -1);
        group = N;
    }
    void unite(int x, int y) {
        x = root(x); y = root(y);
        if(x != y){
            data[x] += data[y];
            data[y] = x;
            --group;
        }
    }
    int root(int x) { return data[x] < 0 ? x : data[x] = root(data[x]);}
    bool same(int a, int b) { return root(a) == root(b);}
};

struct Edge {
    int from, to, cost;
    Edge(int f, int t, int c)
         : from(f), to(t), cost(c) {}
    bool operator< (const Edge& E) const {
        return cost < E.cost;
    }
};
int N, M, K;
int Kruskal(vector<int>& used, UnionFind& uf, const vector<Edge>& edge) {
    int res = 0;
    rep(i, edge.size()) {
        const Edge& e = edge[i];
        if(uf.same(e.from, e.to)) continue;
        uf.unite(e.from, e.to);
        res += e.cost;
        used.push_back(i);
    }
    return res;
}

int solve(const vector<Edge>& edgea, const vector<Edge>& edgeb) {
    if(edgea.size() < K || edgeb.size() < (N - K - 1)) return -1;
    UnionFind uf(N); int res = 0;
    vector<int> useda, usedb;
    res += Kruskal(useda, uf, edgea);
    if(uf.group > 1) {
        res += Kruskal(usedb, uf, edgeb);
        if(uf.group > 1) return -1;
    }
    vector<int> swap_cost;
    vector<bool> used_b(edgeb.size(), false);
    for(int k : usedb) used_b[k] = true;
    for(int k : useda) {
        UnionFind temp(N);
        for(int i : useda) if(i != k)
          temp.unite(edgea[i].from, edgea[i].to);
        for(int i : usedb)
          temp.unite(edgeb[i].from, edgeb[i].to);
        int kawari = INF;
        rep(i, edgeb.size()) {
            if(used_b[i]) continue;
            const Edge& e = edgeb[i];
            if(!temp.same(e.from, e.to)) kawari = min(kawari, e.cost);
        }
        if(kawari != INF) swap_cost.push_back(edgea[k].cost - kawari);
    }
    sort(swap_cost.begin(), swap_cost.end(), greater<int>());
    int need_to_swap = edgea.size() - K;
    if(need_to_swap > swap_cost.size() || need_to_swap < 1) return -1;
    rep(i, need_to_swap) res -= swap_cost[i];
    return res;
}

int main() {
    cin.tie(0); ios::sync_with_stdio(false);
    while(cin >> N >> M >> K && N) {
        int u_, v_, w_; char l_;
        vector<Edge> edge_a, edge_b;
        rep(_, M) {
            cin >> u_ >> v_ >> w_ >> l_;
            --u_; --v_;
            if(l_ == 'A') edge_a.emplace_back(u_, v_, w_);
            else edge_b.emplace_back(u_, v_, w_);
        }
        sort(edge_a.begin(), edge_a.end());
        sort(edge_b.begin(), edge_b.end());
        cout << solve(edge_a, edge_b) << endl;
    }
    return 0;
}

