	return a.X.isa;
}

state kruskal(int v, int e, int a, int k){
  state res; res.cost = 0; res.k = 0; res.nk = 0;
  int i, j;
  unionfind U; U.use(v);
  rep(i, v)rep(j, G[i].size()){
    if(G[i][j].isa)G[i][j].cost += a;
    lis.pb(mp(G[i][j], i));
    if(G[i][j].isa)G[i][j].cost -= a;
  }
  sort(all(lis), mycom);
  for(i = 0;i < e;i++){
    pair<edge, int> tmp = lis[i];
    if(!U.findset(tmp.X.to, tmp.Y)){
			if(res.k == k && tmp.X.isa)continue;
			if(res.nk == v - 1 - k && !tmp.X.isa)continue;
      res.cost += tmp.X.cost;
      U.unionset(tmp.X.to, tmp.Y);
      if(tmp.X.isa)res.k++;
      else res.nk++;
    }
  }
  lis.clear();
  return res;
}

