using namespace std;
typedef long long ll;

using Weight = ll;
struct Edge {
	int src, dst; Weight weight;
	char id;
	Edge() {}
	Edge(int s, int d, Weight w, char id) : src(s), dst(d), weight(w), id(id) {}
};
using Edges = vector<Edge>;

struct UnionFind {
	vector<int> par;
	int cnt;
	UnionFind(int size_) : par(size_, -1), cnt(size_) {}
	void unite(int x, int y) {
		if((x = find(x)) != (y = find(y))) {
			if(par[y] < par[x]) swap(x, y);
			par[x] += par[y]; par[y] = x; cnt--;
		}
	}
	bool same(int x, int y) { return find(x) == find(y); }
	int find(int x) { return par[x] < 0 ? x : par[x] = find(par[x]); }
	int size(int x) { return -par[find(x)]; }
	int size() { return cnt; }
};

bool operator < (const Edge &e, const Edge &f) {
	if(e.weight != f.weight) return e.weight < f.weight;
	return e.id > f.id;
}
pair<Weight, Edges> kruskal(int N, Edges es) {
	UnionFind uf(N);
	sort(es.begin(), es.end());
	Weight total = 0; Edges T;
	for(auto &e : es) if(!uf.same(e.src, e.dst)) {
		T.push_back(e); total += e.weight; uf.unite(e.src, e.dst);
	}
	return make_pair(total, T);
}

int main() {
	cin.tie(0);
	ios::sync_with_stdio(false);

	int N, M, K;
	while(cin >> N >> M >> K, N) {
		Edges es;
		UnionFind uf(N);
		for(int i = 0; i < M; i++) {
			int u, v, w;
			char l;
			cin >> u >> v >> w >> l;
			u--, v--;
			es.push_back(Edge(u, v, w, l));
			uf.unite(u, v);
		}

		if(uf.size(0) != N) {
			cout << -1 << endl;
			continue;
		}

		const ll INF = 1LL << 30;
		ll ng = -INF, ok = INF;
		while(abs(ng - ok) > 1) {
			ll mid = (ng + ok) / 2;
			for(int i = 0; i < M; i++) {
				if(es[i].id == 'A') es[i].weight += mid;
			}

			auto res = kruskal(N, es);
			int sum = 0;
			for(auto e : res.second) {
				if(e.id == 'A') sum++;
			}
			if(sum <= K) ok = mid;
			else ng = mid;

			for(int i = 0; i < M; i++) {
				if(es[i].id == 'A') es[i].weight -= mid;
			}
		}

		for(int i = 0; i < M; i++) {
			if(es[i].id == 'A') es[i].weight += ok;
		}
		auto resR = kruskal(N, es);
		for(int i = 0; i < M; i++) {
			if(es[i].id == 'A') es[i].weight--;
		}
		auto resL = kruskal(N, es);

		int sumR = 0, sumL = 0;
		for(auto e : resR.second) {
			if(e.id == 'A') sumR++;
		}
		for(auto e : resL.second) {
			if(e.id == 'A') sumL++;
		}
		if(sumR <= K && K <= sumL) {
			cout << resR.first - ok * K << endl;
		}
		else {
			cout << -1 << endl;
		}
	}
}

#include <bits/stdc++.h>
using namespace std;

class union_find {
private:
	vector<int> uf;

	int find(int x) {
		return uf[x] < 0 ? x : uf[x] = find(uf[x]);
	}

public:
	union_find(int n):uf(n, -1) {}

	bool unite(int x, int y) {
		x = find(x);
		y = find(y);
		if(x == y) return false;

		if(uf[x] > uf[y]) swap(x, y);
		else if(uf[x] == uf[y]) --uf[x];

		uf[y] = x;
		return true;
	}

	int number_of_sets() {
		return count_if(uf.begin(), uf.end(), [](const int &x){return x < 0;});
	}
};

typedef tuple<int, char, int, int> edge; // cost, color, u, v

pair<int, int> kruskal(const int n, vector<edge> &edges, const int offset_a) {
	union_find uf(n);
	int num_a = 0;
	int res = 0;

	const auto calc = [&](const edge &e) { return make_pair(get<0>(e) + (get<1>(e) == 'A' ? offset_a : 0), -get<1>(e)); };
	sort(begin(edges), end(edges), [&](const edge &a, const edge &b) { return calc(a) < calc(b); });

	for(const auto &e : edges) {
		if(uf.unite(get<2>(e), get<3>(e))) {
			res += calc(e).first;
			if(get<1>(e) == 'A') ++num_a;
		}
	}

	return make_pair(num_a, res);
}

int solve(const int n, const int m, const int k) {
	vector<edge> edges;
	edges.reserve(m);

	union_find uf(n);
	for(int i = 0; i < m; ++i) {
		int u, v, w;
		char l;
		cin >> u >> v >> w >> l;
		--u; --v;

		edges.emplace_back(w, l, u, v);
		uf.unite(u, v);
	}

	if(uf.number_of_sets() > 1) return -1;

	constexpr int INF = (1 << 20);
	int L = -INF, R = INF;
	while(L + 1 < R) {
		const int M = (L + R) / 2;
		if(kruskal(n, edges, M).first <= k) {
			R = M;
		}
		else {
			L = M;
		}
	}

	const auto result_R = kruskal(n, edges, R);
	const auto result_L = kruskal(n, edges, L);

	if(!(result_R.first <= k && k <= result_L.first)) return -1;
	return result_R.second - R * k;
}


int main() {
	cin.tie(nullptr);
	ios::sync_with_stdio(false);

	for(int n, m, k; cin >> n >> m >> k && n;) {
		cout << solve(n, m, k) << endl;
	}

	return EXIT_SUCCESS;
}

