struct UnionFind{
  vector<int> data, tree_size;
  UnionFind(int s):data(s,-1),tree_size(s,1) {}

  int root(int x){
    if(data[x]==-1) return x;
    return data[x]=root(data[x]);
  }

  bool set(int x,int y){
    x=root(x);
    y=root(y);
    if(x==y) return false;
    data[y]=x;
    tree_size[x] += tree_size[y];
    tree_size[y] = 0;
    return true;
  }

  bool check(int x,int y){
    x=root(x);
    y=root(y);
    return x==y;
  }

  int size(int x){
    return tree_size[root(x)];
  }

};


int n, m, k;
int u[SIZE], v[SIZE], w[SIZE], l[SIZE];

int check(int x, int f){
  vector<pair<int,int> > vec;
  int sum = 0;

  for(int i=0;i<m;i++){
    vec.push_back({(w[i] + x * !l[i])*2 + (l[i]^f), i});
  }

  sort(vec.begin(), vec.end());

  int counter = 0, Acounter = 0;
  UnionFind uf(n);

  for(int i=0;i<m;i++){
    if(uf.set(u[vec[i].second], v[vec[i].second])){
      counter++;
      Acounter += !l[vec[i].second];
      sum += w[vec[i].second];
    }
  }

  if(counter == n-1)
    return Acounter;
  else
    return -1;
}

int make(int x, int f){
  vector<pair<int,int> > vec;
  int sum = 0;

  for(int i=0;i<m;i++){
    vec.push_back({(w[i] + x * !l[i])*2 + (l[i]^f), i});
  }

  sort(vec.begin(), vec.end());

  int counter = 0, Acounter = 0;
  int limit = k;
  UnionFind uf(n);

  for(int i=0;i<m;i++){
    if(limit <= 0 && !l[vec[i].second]) continue;
    if(uf.set(u[vec[i].second], v[vec[i].second])){
      counter++;
      Acounter += !l[vec[i].second];
      sum += w[vec[i].second];
      limit -= !l[vec[i].second];
    }
  }

  return sum;
}

