using namespace std;

#define mp(x, y) make_pair(x, y)
typedef pair<int, int> pi;
typedef pair<pi, pi> pipi;

class UnionFind{
private:
  vector<int> rank;
  vector<int> par;
public:  
  UnionFind(int n){
    rank.resize(n);
    par.resize(n);
    for(int i=0;i<n;i++){
      rank[i] = 0;
      par[i] = i;
    }
  }
  int find(int x){
    if(par[x] == x) return x;
    return par[x] = find(par[x]);
  }

  int same(int x, int y){
    return find(x) == find(y);
  }

  void unite(int x, int y){
    x = find(x);
    y = find(y);
    if(x == y) return;
    if(rank[x] < rank[y]){
      par[x] = y;
    }else{
      par[y] = x;
      if(rank[x] == rank[y]) rank[x]++;
    }
  }
};

int n, m, k;
vector<pipi>B;

pi kruscal(int a){
  UnionFind uf(n);
  int cnt = 0, cost = 0;
  for(int i=0;i<B.size();i++){
    if(!uf.same(B[i].second.first, B[i].second.second)){
      if(B[i].first.second == 'A' && cnt >= k) continue;
      cost += B[i].first.first;
      if (B[i].first.second == 'A'){
	cnt++;
	cost -= a;
      }
      uf.unite(B[i].second.first, B[i].second.second);
    }
  }
  return mp(cost, cnt);
}

pi solve(int a){
  for(int i=0;i<B.size();i++) if(B[i].first.second == 'A') B[i].first.first += a;
  sort(B.begin(), B.end());
  pi ret = kruscal(a);
  for(int i=0;i<B.size();i++) if(B[i].first.second == 'A') B[i].first.first -= a;
  return ret;
}

int is_connected(){
  UnionFind uf(n);
  //  cout << "B " << B.size() << endl;  
  for(int i=0;i<B.size();i++)
    if(!uf.same(B[i].second.first, B[i].second.second)){
      uf.unite(B[i].second.first, B[i].second.second);
      //      cout << B[i].second.first << " " << B[i].second.second << endl;
    }
  set<int>s;

  for(int i=0;i<B.size();i++){
    //    cout << uf.find(i) << endl;
    s.insert(uf.find(i));
  }
  return s.size() == 1;
}

int main(){
  while(1){
    B.clear();
    cin >> n >> m >> k;
    if(n == 0 && m == 0 && k == 0) break;
    int acnt = 0, bcnt = 0;
    for(int i=0;i<m;i++){
      int u, v, w;string l;
      cin >> u >> v >> w >> l; 
      B.push_back(make_pair(make_pair(w, l[0]), make_pair(u-1, v-1)));
      if(l[0] == 'A') acnt++;
      else bcnt++;
    }
    //    cout << acnt << " " << bcnt << " " << is_connected() << endl;
    if(acnt < k || bcnt < n-1-k || !is_connected()){
      cout << -1 << endl;
      continue;
    }
    int res = 1000000000;
    int mina = -10000000, maxa = 100000000;
    int mid = (mina+maxa)/2;
    for(int i=0;i<100;i++){
      pi ret = solve(mid);
      //      cout << maxa << " " << mina<< "  "<< ret.first << " " << ret.second<< endl;
      if(ret.second < k){
	maxa = mid-1;
      }else if(ret.second > k){
	mina = mid+1;
      }else{
	res = min(res, ret.first);
	mina = mid;
      }
      mid = (maxa+mina)/2;
    }
    if(res == 1000000000)
      cout << -1 << endl;
    else
      cout << res << endl;
  }
  return 0;
}

