P kruskal() {
	sort(cop, cop + E, comp);
	init(V);
	int res = 0; int cost = 0;
	for (int i = 0; i < E; i++) {
		edge e = cop[i];
		if (e.col == 0 && res == k)continue;
		if (!same(e.u, e.v)) {
			unite1(e.u, e.v);
			cost += e.cost;
			if (e.col == 0) {
				res++;
			}
		}
	}
	return { res,cost };
}
edge ori[600];
void init2() {
	rep(i, E) {
		cop[i] = ori[i];
	}
	return;
}
bool ooo(int x) {
	init2();
	rep(i, E) {
		if (cop[i].col == 0) {
			cop[i].cost += x;
		}
	}
	P r = kruskal();
	if (r.first < k) {
		return false;
	}
	return true;
}
int main() {
