			ori[i] = { x,y,z,w - 'A' };
		}
		while (ri - le > 1) {
			int mid = (ri + le) / 2;
			if (ooo(mid)) {
				le = mid;
			}
			else {
				ri = mid;
			}
		}
		init2();
		rep(i, E) {
			if (cop[i].col == 0) {
				cop[i].cost += le;
			}
		}
		P fin = kruskal();
		if (fin.first != k) {
			cout << -1 << endl; continue;
		}
		bool f = true;
		rep(i, V) {
			rep(j, V) {
				if (!same(i, j)) {
					f = false;
				}
			}
		}
		if (!f) {
