const int M = 1000000007;

int main() {
    while (1) {
        int n, m, k;
        cin >> n >> m >> k;
        if (!n) return 0;
        vector<vector<Pibi>> edge(n);
        for (int j = 0; j < m; ++j) {
            int u, v, w;
            char c;
            cin >> u >> v >> w >> c;
            --u;
            --v;
            edge[u].push_back(Pibi(Pib(w, c == 'A'), v));
            edge[v].push_back(Pibi(Pib(w, c == 'A'), u));
        }
        int l = -200, r = 200;
        int ans = -1;
        while (l <= r) {
            int mid = (l + r) / 2;
            priority_queue<Pibi, vector<Pibi>, greater<Pibi>> q;
            vector<bool> vis(n);
            q.push(Pibi(Pib(0, false), 0));
            int sum = 0;
            int ca = 0;
            int c = 0;
            while (!q.empty()) {
                auto p = q.top();
                q.pop();
                if (vis[p.second]) continue;

                vis[p.second] = true;
                sum += p.first.first;
                ++c;
                ca += p.first.second;
                for (auto a : edge[p.second]) {
                    if (a.first.second)
                        q.push(Pibi(Pib(a.first.first + mid, a.first.second), a.second));
                    else
                        q.push(a);
                }
            }
            if (c != n) {
                ans = -1;
                break;
            }
            if (ca <= k)
                ans = sum - mid * k;
            if (ca == k) {
                break;
            }
            else if (ca < k) {
                r = mid - 1;
            }
            else {
                l = mid + 1;
            }
        }
        if (r < -190 || 190 < l)
            ans = -1;

        cout << ans << "\n";
    }
}

