#include<bits/stdc++.h>
using namespace std;
using Int = long long;

struct Kruskal{

  struct UnionFind{
    Int n;
    vector<Int> r,p;
    UnionFind(){}
    UnionFind(Int sz):n(sz),r(sz,1),p(sz,0){iota(p.begin(),p.end(),0);}
    Int find(Int x){
      return (x==p[x]?x:p[x]=find(p[x]));
    }
    bool same(Int x,Int y){
      return find(x)==find(y);
    }
    void unite(Int x,Int y){
      x=find(x);y=find(y);
      if(x==y) return;
      if(r[x]<r[y]) swap(x,y);
      r[x]+=r[y];
      p[y]=x;
    }
  };

  struct edge{
    Int from,to;
    double cost;
    Int used;
    char color;
    edge(){}
    edge(Int from,Int to,double cost,char color):
      from(from),to(to),cost(cost),used(0),color(color){}
    bool operator<(const edge& e) const{
      return cost<e.cost;
    }
  };

  Int n;
  vector<edge> edges;

  Kruskal(){}
  Kruskal(Int sz):n(sz){}

  Int build(Int k=-1){
    sort(edges.begin(),edges.end());
    UnionFind uf(n+1);
    double res=0;
    Int cnt=0;
    for(Int i=0;i<(Int)edges.size();i++){
      edge &e=edges[i];
      if(!uf.same(e.from,e.to)){
	e.used=1;
	res+=e.cost;
	uf.unite(e.from,e.to);
	cnt++;
      }
    }
    if(cnt+1!=n) return -1;
    return res;
  }
};

