using namespace std;

constexpr int MAX = 252;

class Union_Find {
  public:
    int par[MAX], rank[MAX];

    Union_Find(int N) {
        for (int i = 0; i < N; i++) {
            par[i] = i;
            rank[i] = 0;
        }
    }

    int find(int x)
    {
        if (par[x] == x) {
            return x;
        }
        return par[x] = find(par[x]);
    }

    void unite(int x, int y)
    {
        x = find(x);
        y = find(y);
        if (x == y) return;

        if (rank[x] < rank[y]) {
            par[x] = y;
        } else {
            par[y] = x;
            if (rank[x] == rank[y]) {
                rank[x]++;
            }
        }
    }

    bool same(int x, int y)
    {
        return (find(x) == find(y));
    }
};

struct edge {
    int u, v, cost, col;
    edge() {}
    edge(int u, int v, int cost, int col) :
        u{u}, v{v}, cost{cost}, col{col} {}

    bool operator < (const edge& e) const {        
        return cost < e.cost;
    }
};

int N, M, K;

void add_cost(int x, vector<edge>& es)
{
    for (auto& e : es) {
        if (e.col == 0) {
            e.cost += x;
        }
    }
}

pair<int, int> kruskal(int x, vector<edge> es)
{
    add_cost(x, es);
    sort(es.begin(), es.end());

    Union_Find uf(N);
    int res = 0, k = 0, en = 0;
    for (const auto& e : es) {
        if (k < K && e.col == 0 && !uf.same(e.u, e.v)) {
            uf.unite(e.u, e.v);     
            res += e.cost - x;
            k++;
            en++;
        } else if (e.col == 1 && !uf.same(e.u, e.v)) {
            uf.unite(e.u, e.v);     
            res += e.cost;
            en++;
        }
    }
    if (en != N - 1) return make_pair(-1, -1);
    return make_pair(res, k);
}

void solve(const vector<edge>& es)
{
    int l = 0, r = 252521;
    while (r - l > 1) {
        int mid = (l + r) / 2;            
        auto mst = kruskal(mid, es);
        if (mst.second < K) {
            r = mid;
        } else {
            l = mid;
        }
    }

    auto l1 = kruskal(l, es);
    auto r1 = kruskal(r, es);
    if (l1.second == K) {
        cout << l1.first << endl;
        return;
    } else if (r1.second == K) {
        cout << r1.first << endl;
        return;
    }

    l = -252521, r = 0;
    while (r - l > 1) {
        int mid = (l + r) / 2;
        auto mst = kruskal(mid, es);
        if (mst.second < K) {
            r = mid;
        } else {
            l = mid;
        }
    }

    auto l2 = kruskal(l, es);
    auto r2 = kruskal(r, es);
    if (l2.second == K) {
        cout << l2.first << endl;
    } else if (r2.second == K) {
        cout << r2.first << endl;
    } else {    
        cout << -1 << endl;
    }
}

int main()
{
    while (cin >> N >> M >> K, N) {
        vector<edge> es;
        for (int i = 0; i < M; i++) {
            int u, v, w;
            char l;
            cin >> u >> v >> w >> l;
            u--; v--;
            es.emplace_back(u, v, w, l - 'A');
        }

        solve(es);       
    }
    return 0;
}

