using Weight=int;
struct Edge{
    int src,dst;
    Weight weight;
    int com;
    Edge(int src,int dst,Weight weight,int com):
    src(src),dst(dst),weight(weight),com(com){}
};

using Edges=vector<Edge>;
using Graph=vector<Edges>;

template<typename T>
struct UnionFind{
    vector<T>data;
    UnionFind(T n):data(n,-1){}
    bool unite(T x,T y){
        x=root(x);y=root(y);
        if(x!=y){
            if(data[y]<data[x])swap(x,y);
            data[x]+=data[y];data[y]=x;
        }
        return x!=y;
    }
    T root(T x){
        return data[x]<0?x:data[x]=root(data[x]);
    }
    bool same(T x,T y){
        return root(x)==root(y);
    }
    T size(T x){
        return -data[root(x)];
    }
};

pair<Weight,int>kruskal(const Graph &g,const int h,const int c){
    int n=g.size();
    UnionFind<int> uf(n);
    auto compare = [c](const Edge &a,const Edge &b) {return a.weight != b.weight ? a.weight>b.weight : a.com == c; };
    priority_queue<Edge,Edges,decltype(compare)>q(compare);
    for(int i=0;i<n;++i){
        for(auto e:g[i]){
            if(i<e.dst){
                if(e.com==c)q.emplace(e.src,e.dst,e.weight+h,e.com);
                else q.push(e);
            }
        }
    }
    int res=0,res1=0;
    Weight total=0;
    Edges forest;
    while(forest.size()<n-1 && !q.empty()){
        Edge e=q.top();q.pop();
        if(uf.unite(e.src,e.dst)){
            forest.push_back(e);
            total+=e.weight;
            if(e.com==c){
                ++res;
            }else{
                ++res1;
            }
        }
    }
    if(res+res1 != n-1)total=-1;
    return pair<Weight, int>(total,res);
}

int main(){
    int v,e,k;
    while (cin >> v >> e >> k, v)
    {
        Graph g(v);
        for (int i = 0; i < e; ++i)
        {
            int s, t, w, l;
            char c;
            cin >> s >> t >> w >> c;
            --s;
            --t;
            l = c;
            g[s].emplace_back(s, t, w, l);
            g[t].emplace_back(t, s, w, l);
        }
        int c;
        if (kruskal(g, 0, 'A').second > k){
            c = 'A';
        }else{
            c = 'B';
        }
        //cout<<c<<endl;
        for (int h = 0; h < 1000; ++h)
        {
            pair<Weight, int> p = kruskal(g, h, c);
            if(p.first == -1){
                cout<<"-1"<<endl;
                break;
            }
            if (p.second <= k && c == 'A')
            {
                cout << p.first-k*h << endl;
                break;
            }
            else if (p.second <= v - k - 1 && c == 'B')
            {
                cout << p.first-(v-k-1)*h << endl;
                break;
            }
            if (h == 999){
                cout << "-1" << endl;
            }
        }
    }
    return 0;
}

