using namespace std;

struct UnionFind
{
  vector< int > data;

  UnionFind(int sz)
  {
    data.assign(sz, -1);
  }

  void unite(int x, int y)
  {
    x = find(x), y = find(y);
    if(x != y) {
      if(data[x] > data[y]) swap(x, y);
      data[x] += data[y];
      data[y] = x;
    }
  }

  int find(int k)
  {
    if(data[k] < 0) return (k);
    return (data[k] = find(data[k]));
  }
};

struct edge
{
  int u, v, cost, used;
  bool company;

  bool operator<(const edge& e)
  {
    if(company != e.company) return (company < e.company);
    return (cost < e.cost);
  }
};

int main()
{
  int N, M, K;
  edge e[600];

  while(scanf("%d %d %d", &N, &M, &K), N) {
    int B = 0;
    for(int i = 0; i < M; i++) {
      int u, v, w;
      char l;
      scanf("%d %d %d %c", &u, &v, &w, &l);
      e[i] = (edge) {--u, --v, w, false, l == 'A'};
      B += l == 'B';
    }
    sort(e, e + M);

    UnionFind tree(N);
    int cost = 0, ac = 0, bc = 0;
    for(int i = 0; i < M; i++) {
      if(tree.find(e[i].u) != tree.find(e[i].v)) {
        tree.unite(e[i].u, e[i].v);
        cost += e[i].cost;
        e[i].used = true;
        i < B ? ++bc : ++ac;
      }
    }

    while(ac < K) {
      tuple< int, int, int > flip(INT_MIN, -1, -1);
      for(int i = 0; i < B; i++) {
        if(!e[i].used) continue;
        UnionFind tree2(N);
        for(int j = 0; j < M; j++) {
          if(i != j && e[j].used) tree2.unite(e[j].u, e[j].v);
        }
        for(int j = B; j < M; j++) {
          if(tree2.find(e[j].u) != tree2.find(e[j].v)) {
            flip = max(flip, make_tuple(e[i].cost - e[j].cost, i, j));
          }
        }
      }
      int costt, x, y;
      tie(costt, x, y) = flip;
      if(costt == INT_MIN) break;
      swap(e[x].used, e[y].used);
      cost -= costt;
      ++ac, --bc;
    }
    if(ac + bc != N - 1 || ac != K) puts("-1");
    else printf("%d\n", cost);
  }
}

