#define mp(x, y) make_pair(x, y)
typedef pair<int, int> pi;
typedef pair<pi, pi> pipi;

class UnionFind{
private:
  vector<int> rank;
  vector<int> par;
public:  
  UnionFind(int n){
    rank.resize(n);
    par.resize(n);
    for(int i=0;i<n;i++){
      rank[i] = 0;
      par[i] = i;
    }
  }
  int find(int x){
    if(par[x] == x) return x;
    return par[x] = find(par[x]);
  }

  int same(int x, int y){
    return find(x) == find(y);
  }

  void unite(int x, int y){
    x = find(x);
    y = find(y);
    if(x == y) return;
    if(rank[x] < rank[y]){
      par[x] = y;
    }else{
      par[y] = x;
      if(rank[x] == rank[y]) rank[x]++;
    }
  }
};

int n, m, k;
vector<pipi>B;

pi kruscal(int a){
  UnionFind uf(n);
  int cnt = 0, cost = 0;
  for(int i=0;i<B.size();i++){
    if(!uf.same(B[i].second.first, B[i].second.second)){
      if(B[i].first.second == 'A' && cnt >= k) continue;
      cost += B[i].first.first;
      if (B[i].first.second == 'A'){
	cnt++;
	cost -= a;
      }
      uf.unite(B[i].second.first, B[i].second.second);
    }
  }
  return mp(cost, cnt);
}

pi solve(int a){
  for(int i=0;i<B.size();i++) if(B[i].first.second == 'A') B[i].first.first += a;
  sort(B.begin(), B.end());
  pi ret = kruscal(a);
  for(int i=0;i<B.size();i++) if(B[i].first.second == 'A') B[i].first.first -= a;
  return ret;
}

int is_connected(){
  UnionFind uf(n);
  //  cout << "B " << B.size() << endl;  
  for(int i=0;i<B.size();i++)
    if(!uf.same(B[i].second.first, B[i].second.second)){
      uf.unite(B[i].second.first, B[i].second.second);
      //      cout << B[i].second.first << " " << B[i].second.second << endl;
    }
  set<int>s;

  for(int i=0;i<B.size();i++){
    //    cout << uf.find(i) << endl;
    s.insert(uf.find(i));
  }
  return s.size() == 1;
}

int main(){
  while(1){
    B.clear();
    cin >> n >> m >> k;
    if(n == 0 && m == 0 && k == 0) break;
    int acnt = 0, bcnt = 0;
    for(int i=0;i<m;i++){
      int u, v, w;string l;
      cin >> u >> v >> w >> l; 
      B.push_back(make_pair(make_pair(w, l[0]), make_pair(u-1, v-1)));
      if(l[0] == 'A') acnt++;
      else bcnt++;
    }
    //    cout << acnt << " " << bcnt << " " << is_connected() << endl;
    if(acnt < k || bcnt < n-1-k || !is_connected()){
      cout << -1 << endl;
      continue;
    }
    int res = 1000000000;
    int mina = -1000000, maxa = 10000000;
    int mid = (mina+maxa)/2;
    while(mina < maxa){
      pi ret = solve(mid);
      //      cout << maxa << " " << mina<< "  "<< ret.first << " " << ret.second<< endl;
      if(ret.second < k){
	maxa = mid-1;
      }else if(ret.second > k){
	mina = mid+1;
      }else{
	res = min(res, ret.first);
	mina = mid;
      }
      mid = (maxa+mina)/2;
    }
    if(res == 1000000000)
      cout << -1 << endl;
    else
      cout << res << endl;
  }
  return 0;
}

#include <bits/stdc++.h>

#define whole(xs) xs.begin(),xs.end()

using namespace std;

namespace {

    typedef double real;
    typedef long long ll;

    template<class T> ostream& operator<<(ostream& os, const vector<T>& vs) {
        if (vs.empty()) return os << "[]";
        os << "[" << vs[0];
        for (int i = 1; i < int(vs.size()); i++) os << " " << vs[i];
        return os << "]";
    }
    template<class T> istream& operator>>(istream& is, vector<T>& vs) {
        for (auto it = vs.begin(); it != vs.end(); it++) is >> *it;
        return is;
    }

    struct Edge {
        int from, to;
        int cost;
        char firm;
        Edge() {}
        Edge(int from, int to, int cost, char firm) : from(from), to(to), cost(cost), firm(firm) {}
    };
    bool operator<(const Edge& a, const Edge& b) {
        if (a.cost == b.cost) return a.firm > b.firm;
        return a.cost < b.cost;
    }
    ostream& operator<<(ostream& os, const Edge& e) {
        return os << "Edge(" << e.from << "->" << e.to << " " << e.cost << " " << e.firm << ")";
    }

    int N, M, K;
    vector<Edge> es;
    bool input() {
        cin >> N >> M >> K;
        if (N == 0 && M == 0 && K == 0) return false;
        es.clear();
        for (int i = 0; i < M; i++) {
            int u, v, w; char l;
            cin >> u >> v >> w >> l;
            u--; v--;
            es.emplace_back(u, v, w, l);
        }
        return true;
    }

    struct UnionFind {
        int N;
        vector<int> P;
        UnionFind(int N) : N(N) {
            P.clear(); P.resize(N, -1);
        }
        int root(int x) {
            if (P[x] == -1) return x;
            return P[x] = root(P[x]);
        }
        int query(int x, int y) {
            return root(x) == root(y);
        }
        void merge(int x, int y) {
            x = root(x); y = root(y);
            if (x == y) return;
            P[x] = y;
        }
    };

    const int INF = 1<<20;

    int countA(int k) {
        vector<Edge> fs = es;
        for (int i = 0; i < M; i++) {
            Edge& f = fs[i];
            if (f.firm == 'A') f.cost += k;
        }
        sort(whole(fs));
        UnionFind uf(N);
        int count = 0;
        for (int i = 0; i < M; i++) {
            const Edge& f = fs[i];
            if (uf.query(f.from, f.to)) continue;
            uf.merge(f.from, f.to);
            if (f.firm == 'A') count++;
        }
        for (int i = 0; i < N; i++) {
            for (int j = i + 1; j < N; j++) {
                if (not uf.query(i, j)) throw(-1);
            }
        }
        return count;
    }

    int cost(int k) {
        vector<Edge> fs = es;
        for (int i = 0; i < M; i++) {
            Edge& f = fs[i];
            if (f.firm == 'A') f.cost += k;
        }
        int count = 0;
        sort(whole(fs));
        UnionFind uf(N);
        int ret = 0;
        for (int i = 0; i < M; i++) {
            const Edge& f = fs[i];
            if (uf.query(f.from, f.to)) continue;
            if (f.firm == 'A') {
                if (count == K) continue;
                count++;
            }
            uf.merge(f.from, f.to);
            ret += f.cost - (f.firm == 'A' ? k : 0);
        }
        return ret;
    }


    void solve() {
        int lb = -INF, ub = INF;
        int p, q;
        try {
            p = countA(lb); q = countA(ub);
        } catch (int e) {
            cout << -1 << endl;
            return;
        }
        if ( (p - K) * (q - K) > 0 ) {
            cout << -1 << endl;
            return;
        }
        while (lb + 1 < ub) {
            int mid = (lb + ub) / 2;
            //cerr << mid << " -> " << countA(mid) << endl;
            if (countA(mid) >= K) {
                lb = mid;
            } else {
                ub = mid;
            }
        }
        cout << cost(lb) << endl;
    }
}

int main() {
    while (input()) solve();
    return 0;
}

#include <iostream>
#include <algorithm>
#include <vector>

using namespace std;

constexpr int MAX = 252;

class Union_Find {
  public:
    int par[MAX], rank[MAX], gnum;

    Union_Find(int N) {
        for (int i = 0; i < N; i++) {
            par[i] = i;
            rank[i] = 0;
        }
        gnum = N;
    }

    int find(int x)
    {
        if (par[x] == x) {
            return x;
        }
        return par[x] = find(par[x]);
    }

    void unite(int x, int y)
    {
        x = find(x);
        y = find(y);
        if (x == y) return;

        if (rank[x] < rank[y]) {
            par[x] = y;
        } else {
            par[y] = x;
            if (rank[x] == rank[y]) {
                rank[x]++;
            }
        }
        gnum--;
    }

    bool same(int x, int y)
    {
        return (find(x) == find(y));
    }    
};

struct edge {
    int u, v, cost, col;
    edge() {}
    edge(int u, int v, int cost, int col) :
        u{u}, v{v}, cost{cost}, col{col} {}

    bool operator < (const edge& e) const {        
        if (cost != e.cost) {
            return cost < e.cost;
        }
        return col > e.col;
    }
};

int N, M, K;

void add_cost(int x, vector<edge>& es)
{
    for (auto& e : es) {
        if (e.col == 0) {
            e.cost += x;
        }
    }
}

pair<int, int> kruskal(int x, vector<edge> es)
{
    add_cost(x, es);
    sort(es.begin(), es.end());    

    Union_Find uf(N);
    int res = 0, k = 0;    
    for (const auto& e : es) {
        if (!uf.same(e.u, e.v)) {
            uf.unite(e.u, e.v);     
            res += e.cost;
            k += (e.col == 0);
        }
    }
    return make_pair(res, k);
}

bool unconnected(const vector<edge>& es)
{
    Union_Find uf(N);
    for (const auto& e : es) {
        if (!uf.same(e.u, e.v)) {
            uf.unite(e.u, e.v);
        }
    }
    return (uf.gnum != 1);
}

void solve(const vector<edge>& es)
{
    constexpr int INF = 252521;    
    int l = -INF, r = INF;

    if (unconnected(es)) {
        cout << -1 << endl;
        return;
    }

    while (r - l > 1) {
        int mid = (l + r) / 2;            
        auto mst = kruskal(mid, es);

        if (mst.second < K) {
            r = mid;
        } else {
            l = mid;
        }
    }

    auto ll = kruskal(l, es);
    auto rr = kruskal(r, es);

    if (rr.second <= K && K <= ll.second) {
        cout << rr.first - r * K << endl;
    } else {
        cout << -1 << endl;
    }
}

int main()
{
    while (cin >> N >> M >> K, N) {
        vector<edge> es;
        for (int i = 0; i < M; i++) {
            int u, v, w;
            char l;
            cin >> u >> v >> w >> l;
            u--; v--;
            es.emplace_back(u, v, w, l - 'A');
        }
        solve(es);       
    }
    return 0;
}

